		<style media="screen">
		html{
			height: 100%;
		}
		body{
			width: 100%;
			height: 100%;
			background: url('/img/svg/tree1.svg') no-repeat;
			background-position: center bottom;
		}
		*{
			margin: 0;
			padding: 0;
		}
		.preloder-letter{
			transform: translateY(100%);
			max-width: 80px;
		}
		.preloder-letter__animated{
			transition: 1.6s;
			-webkit-transition: 1.6s;
			max-width: 300px;
		}
		.grid{
			position: absolute;
			top: 50%;
			left: 50%;
			transform: translate(-50%, -50%);
		}
		.action:focus {
			outline: none;
		}
		.particles-canvas {
			position: absolute;
			pointer-events: none;
			top: 50%;
			left: 50%;
			transform: translate3d(-50%,-50%,0);
		}
		.particles-wrapper {
			position: relative;
			display: inline-block;
			overflow: hidden;
			will-change: transform;
		}

		</style>
		<script src='js/anime.min.js'></script>
		<script type="text/javascript">


		(function particlesBuild() {

		    const arrOpts =
		        {
		            type: 'rectangle',
		            direction: 'top',
		            duration: 1600,
		            easing: [0.2,1,0.7,1],
		            particlesAmountCoefficient: 6,
		            oscillationCoefficient: 3,
		            color:'#ffb519',
		            size: 7,
		            speed: 1,
		            // style: 'stroke',
		        };

		    const items = document.querySelectorAll('.grid__item');
		    const bttnBack = document.querySelector('.action');
		    const bttn = document.querySelector('.preloder-letter');

		    let particlesOpts = arrOpts;

		    const particles = new Particles(bttn, particlesOpts);

		        document.addEventListener("DOMContentLoaded", () => {
		            particles.integrate();

						  function addClass() {
							  bttn.classList.add('preloder-letter__animated')
							  }

							  setTimeout(addClass, 3000);
		        });
		})();
		</script>
